var observableModule = require("data/observable");
var frameModule = require("ui/frame");
var ProductModel = require("../../shared/models/product");
var imageModule = require("ui/image");
var cameraModule = require("camera");
var product = new ProductModel();
var pageData = new observableModule.Observable({
    product: product
});
function loaded(args) {
    var page = args.object;
    page.bindingContext = pageData;
    console.log("vista cargada");
}
exports.loaded = loaded;
;
function goToList() {
    console.log("a la lista con la info " + product.name + ", " + product.price);
    var topmost = frameModule.topmost();
    topmost.navigate({
        moduleName: "views/listProducts/listProducts",
        context: product
    });
}
exports.goToList = goToList;
;
function selectImage() {
    cameraModule.takePicture({ width: 300, height: 300, keepAspectRatio: true }).then(function (picture) {
        console.log("Result is an image source instance");
        product.image = new imageModule.Image();
        product.image = picture;
    });
}
exports.selectImage = selectImage;
